import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import rockPaperScissors from './rockPaperScissors';

class App extends Component{
constructor(props) {
         super(props);
         this.state ={
           playerValue: '',
           opponentValue: '',
           history: []
         }


         this.changeValue = this.changeValue.bind(this);
         this.onSubmit = this.onSubmit.bind(this);
    }

    changeValue = event => {
      this.setState({
          ['playerValue']: event.target.value
      });

    };

    pickOpponentValue()
    {
      var num = Math.ceil(Math.random()* 3);
      switch(num){
        case 3: 
          return 'scissors';
        case 2:
          return 'paper';
        default:
          return 'rock';
      }
    }

    determineWin()
    {
      this.setState({
        ['opponentValue']: this.pickOpponentValue()
      });
      if(this.state.playerVal == 'rock' && this.state.opponentValue == 'scissors')
        return 'player';
      if(this.state.playerVal == 'scissors' && this.state.opponentValue == 'paper')
        return 'player';
      if(this.state.playerVal == 'paper' && this.state.opponentValue == 'rock')
        return 'player';
      else 
      return 'opponent';
    }


     onSubmit = event => {
        event.preventDefault();
      let history = this.state.history;
      history.push({
        victor: this.determineWin(),
        player: this.state.playerValue,
        opponent:this.state.opponentValue,
      })
      this.setState(
        {
          ['history']: history
        }

      )

   }
  render(){
  return (
    <div className="App">
      <header className="App-header">
        <p>
          {this.state.playerValue}
        </p>
        <select value={this.state.playerValue} onChange={event => this.changeValue(event)}>
          <option>choose...</option>
          <option value="rock">rock</option>
          <option value="paper">paper</option>
          <option value="scissors">scissors</option>
        </select>
        <input type="submit" value="Submit" onClick={(event)=> this.onSubmit(event)} />
      </header>
      <p>
        {this.state.history}
      </p>
    </div>
  );
  };
}

export default App;
